import React, {useState, useRef} from 'react';
import {Button, Modal} from 'react-bootstrap';
import 'filepond/dist/filepond.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//link - /CSE442-542/2019-Fall/cse-442i/template
class TemplateGenerator extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      image: '',
      currentImage: 'testimages/uploadImagePlaceholder.png',
      topText: '',
      bottomText: '',
      isImageGenerated: false,
      isModalOpen: false
    }
  }

   addText = e => {
    if(e.target.name === 'topText'){
      this.setState({topText: e.target.value})
    }else{
      this.setState({bottomText: e.target.value})
    }
  }

  uploadImage = event => {
    this._imgToUpload = event.target.files[0]
  }

  render(){
    return(
      <div>
        <img src={this.currentImage}/>
        <input type="file" onChange={this.uploadImage}/>
        <Button
        variant="primary"
        onClick={this.setState({currentImage: window.URL.createObjectURL(this._imgToUpload)})}>
            Upload
            </Button>

      </div>
    )
  }
}

export default TemplateGenerator;
