import React, {useState, useRef} from 'react';
import {Button, Modal} from 'react-bootstrap';

//link - /CSE442-542/2019-Fall/cse-442i/template
class TemplateGenerator extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      image: '',
      currentImage: '',
      topText: '',
      bottomText: '',
      isImageGenerated: false,
      isModalOpen: false
    }
  }

   addText = e => {
    if(e.target.name === 'topText'){
      this.setState({topText: e.target.value})
    }else{
      setBottomText({bottomText: e.target.value})
    }
  }

  uploadImage = e => {
    this.setState({currentImage: URL.createObjectURL(e.target.files[0])})
  }

  render(){
    return(
      <div>
        <Button
        variant="primary"
        onClick={this.setState({isModalOpen: true})}>
        Create from template
        </Button>
        <Modal show={this.state.isModalOpen} onHide={this.setState({isModalOpen: false})}>
          <Modal.Header closeButton/>
            <Button
            onClick={this.uploadImage}>
            Upload
            </Button>
        </Modal>

      </div>
    )
  }
}

export default TemplateGenerator;
