import React, { useState } from "react";
import Spinner from 'react-bootstrap/Spinner'
import Button from 'react-bootstrap/Button'
import {Link} from 'react-router-dom';
import Jumbotron from "react-bootstrap/Jumbotron";
import Container from "react-bootstrap/Container";


class UploadWait extends React.Component {

    state = {
        progress: "",
    };

    constructor(props) {
        super(props);
        this.resetState();
    }

    isWaiting() {
        return this.state.progress == 0;
    }

    resetState() {
        alert("1");
        this.setState({progress: 0});
        alert("0");
    }

    startLoad() {
        //alert("hi");
        this.setState({progress: 1});
        alert("start");
    }

    endLoad(i) {
        if (i === 0) {
            this.setState({progress: 2});
            alert("Nice")
        } else {
            this.setState({progress: 3});
            alert("Oh")
        }
    }

    renderStatus() {
        if (this.state.progress === 0) {
            return null;
        }
        if (this.state.progress === 1) {
            return (
                <div className={"load_overlay"}>
                    <Spinner animation="border" role="status">
                        <span className="sr-only">Loading...</span>
                    </Spinner>
                </div>
            );
        } else if (this.state.progress === 2) {
            return (
                <Jumbotron fluid>
                    <Container>
                        <h1>Upload Complete!</h1>
                        <p> Go to the homepage to see your upload! </p>
                        <Link to="/CSE442-542/2019-Fall/cse-442i/home">
                            <Button variant="primary">Home</Button>
                        </Link>
                    </Container>
                </Jumbotron>
            );
        } else if (this.state.progress === 3 ) {
            return (
                <Jumbotron fluid>
                    <Container>
                        <h1>Upload Failed</h1>
                        <p> { this.state.progress } </p>
                        <p> Try uploading again! </p>
                        <Link to="/CSE442-542/2019-Fall/cse-442i/upload">
                            <Button onClick={this.resetState()}>Upload</Button>
                        </Link>
                        <Link to="/CSE442-542/2019-Fall/cse-442i/home">
                            <Button onClick={this.resetState()}>Home</Button>
                        </Link>
                    </Container>
                </Jumbotron>
            );
        }
    }

}

export default UploadWait;
